using NUnit.Framework;

namespace EliTools.IllegalWordsDetection.Scripts.UnitTesting.Editor
{
    public class UnitTesting
    {
        [Test]
        public void TestIsExistIllegalWords()
        {
            // 测试敏感词"官方"和"sex"
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("abc"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("a"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("工abc我"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("工abc"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("abc我"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("我"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("我《》？a<工>?"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords(""));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("<>"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords("<"));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords(" "));
            Assert.IsFalse(IllegalWordsDetection.IsExistIllegalWords(" qe工 "));
            
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("官方"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("官《》？，。、：“；‘{}【】|、~·！@#￥%……&*（）——+-=,./;'[]\\<>?:\"{}|`~!@#$%^&*()_+-=方"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords(" 官-=方 "));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords(" 官- =方 "));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords(" adsf官- =方adsf "));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("asdf官方花木成畦手自栽asdf"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("asdf官方asdf"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("我我我官方我我我"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("我我我官方"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("官方我我我"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("，官方我我我"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords(",官方我我我"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,官方"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,sex"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,s&*()ex"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,S&*()ex"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,S&*()Ex"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,S&*()EX"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,s&*()eX"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,s&*()e   X"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("ad我，,s&*()e   X   "));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("sEx"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("sEx "));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords(" sE  x1324asdf"));
            
            // 多个敏感词
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("sex,官方"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("asdfs&*()ex,/*-官  --方asdf"));
            // 带有符号的敏感词, 长度大于8的敏感词
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("www.bin5.cn"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("www.bin5cn"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("wwwbin5cn"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("asaswwwbin5cn我我我"));
            Assert.IsTrue(IllegalWordsDetection.IsExistIllegalWords("asaswww^&*)bi&*()n5^*(cn我我我"));
            
