        [Test]
        public void TestFilter()
        {
            // 测试敏感词"官方"和"sex"
            Assert.AreEqual(IllegalWordsDetection.Filter("abc"), "abc");
            Assert.AreEqual(IllegalWordsDetection.Filter("a"), "a");
            Assert.AreEqual(IllegalWordsDetection.Filter("工abc我"), "工abc我");
            Assert.AreEqual(IllegalWordsDetection.Filter("工abc"), "工abc");
            Assert.AreEqual(IllegalWordsDetection.Filter("abc我"), "abc我");
            Assert.AreEqual(IllegalWordsDetection.Filter("我"), "我");
            Assert.AreEqual(IllegalWordsDetection.Filter("我《》？a<工>?"), "我《》？a<工>?");
            Assert.AreEqual(IllegalWordsDetection.Filter(""), "");
            Assert.AreEqual(IllegalWordsDetection.Filter("<>"), "<>");
            Assert.AreEqual(IllegalWordsDetection.Filter("<"), "<");
            Assert.AreEqual(IllegalWordsDetection.Filter(" "), " ");
            Assert.AreEqual(IllegalWordsDetection.Filter(" qe工 "), " qe工 ");
            
            Assert.AreEqual(IllegalWordsDetection.Filter("官方"), "**");
            Assert.AreEqual(IllegalWordsDetection.Filter("官《》？，。、：“；‘{}【】|、~·！@#￥%……&*（）——+-=,./;'[]\\<>?:\"{}|`~!@#$%^&*()_+-=方")
                ,"*《》？，。、：“；‘{}【】|、~·！@#￥%……&*（）——+-=,./;'[]\\<>?:\"{}|`~!@#$%^&*()_+-=*");
            Assert.AreEqual(IllegalWordsDetection.Filter(" 官-=方 "), " *-=* ");
            Assert.AreEqual(IllegalWordsDetection.Filter(" 官- =方 "), " *- =* ");
